package br.testes.selenium;import static org.junit.Assert.assertEquals;import java.util.List;import java.util.concurrent.TimeUnit;import org.junit.After;import org.junit.Before;import org.junit.Test;import org.openqa.selenium.By;import org.openqa.selenium.WebDriver;import org.openqa.selenium.WebElement;import org.openqa.selenium.htmlunit.HtmlUnitDriver;import org.openqa.selenium.support.ui.Select;public class SeleniumPlaylandTest {	private WebDriver driver;	@Before	public void configurarWebDriver() {		this.driver = new HtmlUnitDriver();		this.driver.manage().timeouts().implicitlyWait(60, TimeUnit.SECONDS);		this.driver.get("http://www.agilecode.com.br/selenium");	}	@Test	public void deveriaVerificarTituloDaPagina() {		String titulo = this.driver.getTitle();		assertEquals("Selenium - Playland", titulo);	}	@Test	public void deveriaVerificarTextoDeUmElementoLocalizadoPorId() {		WebElement elemento = this.driver.findElement(By.id("descricao"));		String texto = elemento.getText();		assertEquals("Selenium Playland", texto);	}	@Test	public void deveriaVerificarOTextoDeUmElementoLocalizadoPorClassName() {		WebElement elemento = this.driver.findElement(By.className("titulo"));		String texto = elemento.getText();		assertEquals("Selenium Playland", texto);	}	@Test	public void deveriaVerificarOTextoDeUmElementoLocalizadoPeloTextoDoLinkParcial() {		WebElement link = this.driver.findElement(By				.partialLinkText("pr칩xima p치gina"));		String texto = link.getText();		assertEquals("Ir para a pr칩xima p치gina", texto);	}	@Test	public void deveriaVerificarOTextoDeUmElementoLocalizadoPorXPath() {		WebElement elemento = driver.findElement(By.xpath("html/body//h1"));		String texto = elemento.getText();		assertEquals("Selenium Playland", texto);	}	@Test	public void deveriaPreencherESubmeterFormulario() {		WebElement user = this.driver.findElement(By.name("campo_user"));		user.sendKeys("root");		WebElement password = this.driver.findElement(By.name("campo_password"));		password.sendKeys("javamagazine");		WebElement formulario = this.driver.findElement(By.name("formulario"));		formulario.submit();		String titulo = this.driver.getTitle();		assertEquals("Selenium - Playland - Sucesso", titulo);	}	@Test	public void deveriaVerificarQuantidadeDeOpcoesDeUmElementoSelect() {		Select elemento = new Select(this.driver.findElement(By				.name("campo_select")));		List<WebElement> options = elemento.getOptions();		assertEquals(3, options.size());	}	@After	public void fecharWebDriver() {		this.driver.close();	}}